mapper.insert:
insert
	into attachment
			 (
			   file_no
			  ,origin_name
			  ,modify_name
			  ,attach_path
			  ,ref_type
			  ,ref_no
			 )
values 
			(
			  seq_ano.nextval
			 ,#{originName}
			 ,#{modifyName}
			 ,#{attachPath}
			 ,#{refType}
			 ,#{refNo}
			)

mapper.select:
select 
	   file_no
	  ,origin_name
	  ,modify_name
	  ,attach_path
  from attachment



			#{fileNo}



		and ref_type = #{refType}

		  #{refNo}

