mapper.select:
select CAL_NO , CAL_SORT , CAL_TITLE , CAL_CONTENT , START_DATE , END_DATE , CAL_PLACE , CAL_COLOR , calender.GROUP_CODE , member.user_NO , code_name , upper_code from calender join tb_group on ( calender.GROUP_CODE = code ) left join co_worker using (cal_no) join member on (calender.REGIST_EMP = member.USER_NO) where cal_sort = 'D' and tb_group.GROUP_CODE in 'CALD01' and member.team_code = #{teamCode} and calender.status = 'Y'
and Months_between(START_DATE , sysdate) <= 6 and Months_between(end_DATE , sysdate) >= -6
#{num}

mapper.insert:
insert 
into 
	calender
(
	CAL_NO
	,CAL_SORT
	,CAL_TITLE
	,CAL_CONTENT
	,START_DATE
	,END_DATE
	,CAL_COLOR
	,REGIST_EMP
	,MODIFY_EMP
	,GROUP_CODE
) values ( 
	SEQ_CALNO.nextval
	,#{calSort}
	,#{calTitle}
	,#{calContent}
	,to_date(#{startDate} 	, 'YYYY-MM-DD HH24:MI:SS')
	,to_date(#{endDate}			, 'YYYY-MM-DD HH24:MI:SS')
	,#{color}
	,#{emp}
	,#{emp}
	,#{groupCode}
	)

mapper.update:
update
	calender
set 
	CAL_TITLE 			= #{calTitle}
	, CAL_CONTENT 	= #{calContent}
	, START_DATE 		= to_date(#{startDate} 	, 'YYYY-MM-DD HH24:MI:SS')
	, END_DATE 			= to_date(#{endDate}	, 'YYYY-MM-DD HH24:MI:SS')
	, CAL_PLACE			= #{place}
	, CAL_COLOR 		= #{color}
	, GROUP_CODE 		= #{groupCode}
	, MODIFY_EMP 		= #{emp}
where
	CAL_NO = #{calNO}

mapper.insert:
insert
into 
	co_worker
(
	WORKER_NO
	, CAL_NO
	, USER_NO
)values(
	SEQ_COWNO.NEXTVAL
	, #{calNO}
	, #{userNo}
)

mapper.select:
select cal_no , cal_color , code_Name , cal_title , to_date(start_Date, 'YYYY-MM-DD')|| ' ~ ' || to_date(end_date, 'YYYY-MM-DD') duraDate from calender join tb_group on (calender.GROUP_CODE = CODE and tb_group.GROUP_CODE = 'CALD02') where cal_sort = #{calSort} and calender.status = 'Y'
and START_DATE >= #{dataStart} and end_DATE <= #{dataEnd}
ORDER by cal_no desc

mapper.update:
	update calender
set 
    cal_title = 		#{calTitle}
    , cal_content = #{calContent}
    , start_date = 	to_date(#{startDate} 	, 'YYYY-MM-DD HH24:MI:SS')
    , end_date = 		to_date(#{endDate} 		, 'YYYY-MM-DD HH24:MI:SS')
    , cal_place = 	#{place}
    , cal_color = 	#{color}
    , modify_emp = 	#{emp}
    , group_code = 	#{groupCode}
where
    cal_no = #{calNO}
