mapper.insert:
  	insert into
    	chatting_room
    	(
        cr_no
        , cr_name
        , cr_enroll_user_no
        , cr_modify_user_no
    	)
	    values
	    (
	        seq_crno.nextval
	        , #{ crName }
	        , #{ userNo }
	        , #{ userNo }
	    )

  	insert into
	    chatting_participation
	    (
	        chat_no
	        , chat_user_no
	        , chat_inv_user_no
	        , cr_no
	    )
	    values
	    (
	        seq_cpno.nextval


		        	, #{ partUserNo }


		        	, #{ userNo }


	        , #{ userNo }


		        	, #{ chatRoomNo }


		        	, seq_crno.currval


	    )

  insert into 
  	chatting_message
	  (
	      msg_no
	      , msg_content
	      , user_no
	      , cr_no
	  )
  values
	  (
	      seq_cmno.nextval
	      , #{ msgContent }
	      , #{ userNo }
	      , #{ chatRoomNo }
	  )
