mapper.select:
select count(attraction_no) "totalAttractions"
  from tb_attraction



			#{location}



		and tb_attraction.status = #{status}


		and attraction_name like '%' || #{keyword} || '%'

select attraction_no "attractionNo" ,location_name "locationName" ,attraction_name "attractionName" ,customer_limit || '명' "customerLimit" ,nvl2(age_limit, age_limit || '세 ' || age_limit_range, '') "ageLimit" ,nvl2(height_limit, height_limit || 'cm ' || height_limit_range, '') "heightLimit" ,tb_attraction.status "status" ,thumbnail_url "thumbnailURL" ,( select user_name from member where user_no = tb_attraction.regist_emp ) "registEmp" ,( select user_name from member where user_no = tb_attraction.modify_emp ) "modifyEmp" from tb_attraction join tb_location on (location = location_no)
#{location}
and tb_attraction.status = #{status}
and attraction_name like '%' || #{keyword} || '%'
order by decode(tb_attraction.status, 'OPERATING', 1), decode(tb_attraction.status, 'PENDING', 2), decode(tb_attraction.status, 'STOP', 3), decode(tb_attraction.status, 'CLOSED', 4), tb_attraction.regist_date desc, tb_attraction.modify_date desc

mapper.insert:
insert 
  into tb_attraction
  	   (
  	    attraction_no
  	   ,location
  	   ,manage_emp
  	   ,regist_emp
  	   ,modify_emp
  	   ,attraction_name
  	   ,attraction_intro
  	   ,customer_limit

  	     ,age_limit
  	     ,age_limit_range


		  	 ,height_limit
		  	 ,height_limit_range

  	   ,status
  	   ,status_reason
  	   ,thumbnail_url
  	   )
 values 
 	   (
 		 seq_atrtno.nextval
 		,#{location}
 		,#{manageEmp}
 		,#{registEmp}
 		,#{modifyEmp}
 		,#{attractionName}
 		,#{attractionIntro}
 		,#{customerLimit}

 		  ,#{ageLimit}
 		  ,#{ageLimitRange}


 		  ,#{heightLimit}
 		  ,#{heightLimitRange}

 		,#{status}
 		,#{statusReason}
 		,#{thumbnailURL}
 	   )

mapper.update:
update tb_attraction
   set location = #{location}
   	  ,modify_emp = #{modifyEmp}
   	  ,attraction_name = #{attractionName}
   	  ,attraction_intro = #{attractionIntro}
   	  ,thumbnail_url = #{thumbnailURL}
   	  ,customer_limit = #{customerLimit}

	   	  ,age_limit = #{ageLimit}
	   	  ,age_limit_range = #{ageLimitRange}


	   	  ,height_limit = #{heightLimit}
	   	  ,height_limit_range = #{heightLimitRange}

   	  ,status = #{status}

   	  	,status_reason = #{statusReason}

   	  ,modify_date = sysdate
 where attraction_no = #{attractionNo}

mapper.select:
SELECT A.ATTRACTION_NO "attractionNo" ,A.ATTRACTION_NAME "attractionName" ,A.LOCATION "locationNo" ,TO_CHAR(( (SELECT COUNT(COUNT(*)) VISITOR_USED FROM ATTRACTION_UTILIZATION AU JOIN VISITOR V ON (AU.VISITOR_NO = V.VISITOR_NO) WHERE EXTRACT(YEAR FROM V.VISIT_DATE) = #{year} AND AU.ATTRACTION_NO = A.ATTRACTION_NO GROUP BY ATTRACTION_NO, AU.VISITOR_NO, V.VISIT_DATE) / (SELECT DECODE(COUNT(VISITOR_NO), 0, 1, COUNT(VISITOR_NO)) TOTAL_VISITORS FROM VISITOR WHERE EXTRACT(YEAR FROM VISIT_DATE) = #{year}) ) * 100, 'FM000.00') "yearUsage" ,TO_CHAR(( (SELECT COUNT(COUNT(*)) VISITOR_USED FROM ATTRACTION_UTILIZATION AU JOIN VISITOR V ON (AU.VISITOR_NO = V.VISITOR_NO) WHERE EXTRACT(YEAR FROM V.VISIT_DATE) = #{year} AND EXTRACT(MONTH FROM V.VISIT_DATE) = #{month} AND AU.ATTRACTION_NO = A.ATTRACTION_NO GROUP BY ATTRACTION_NO, AU.VISITOR_NO, V.VISIT_DATE) / (SELECT DECODE(COUNT(VISITOR_NO), 0, 1, COUNT(VISITOR_NO)) TOTAL_VISITORS FROM VISITOR WHERE EXTRACT(YEAR FROM VISIT_DATE) = #{year} AND EXTRACT(MONTH FROM VISIT_DATE) = #{month}) ) * 100, 'FM000.00') "monthUsage" ,TO_CHAR(( (SELECT COUNT(COUNT(*)) VISITOR_USED FROM ATTRACTION_UTILIZATION AU JOIN VISITOR V ON (AU.VISITOR_NO = V.VISITOR_NO) WHERE EXTRACT(YEAR FROM V.VISIT_DATE) = #{year} AND EXTRACT(MONTH FROM V.VISIT_DATE) = #{month} AND EXTRACT(DAY FROM V.VISIT_DATE) = #{date} AND AU.ATTRACTION_NO = A.ATTRACTION_NO GROUP BY ATTRACTION_NO, AU.VISITOR_NO, V.VISIT_DATE) / (SELECT DECODE(COUNT(VISITOR_NO), 0, 1, COUNT(VISITOR_NO)) TOTAL_VISITORS FROM VISITOR WHERE EXTRACT(YEAR FROM VISIT_DATE) = #{year} AND EXTRACT(MONTH FROM VISIT_DATE) = #{month} AND EXTRACT(DAY FROM VISIT_DATE) = #{date}) ) * 100, 'FM000.00') "dailyUsage" FROM TB_ATTRACTION A WHERE A.STATUS IN ('OPERATING', 'STOP')
AND A.LOCATION = #{locationNo}
ORDER BY "yearUsage" desc, "monthUsage" desc, "dailyUsage" desc

SELECT LPAD(L, 2, 0) L
     , TO_NUMBER(TO_CHAR(((SELECT COUNT(VISITOR_NO)
           				 					 FROM (SELECT DISTINCT TO_CHAR(V.VISIT_DATE, 'YYYYMMDD') VISIT_DATE, A.VISITOR_NO, A.ATTRACTION_NO 
								                     FROM ATTRACTION_UTILIZATION A
								                     JOIN VISITOR V ON (A.VISITOR_NO = V.VISITOR_NO)
			                   					 )
           									WHERE EXTRACT(YEAR FROM TO_DATE(VISIT_DATE, 'YYYYMMDD')) = #{year}


									           	 AND EXTRACT(MONTH FROM TO_DATE(VISIT_DATE, 'YYYYMMDD')) = L


									             AND EXTRACT(MONTH FROM TO_DATE(VISIT_DATE, 'YYYYMMDD')) = #{month}
									             AND EXTRACT(DAY FROM TO_DATE(VISIT_DATE, 'YYYYMMDD')) = L


								               AND ATTRACTION_NO = #{no}
							              ) / (SELECT DECODE(COUNT(VISITOR_NO), 0, 1, COUNT(VISITOR_NO))
							               			 FROM VISITOR
							              			WHERE EXTRACT(YEAR FROM VISIT_DATE) = #{year}


									              		AND EXTRACT(MONTH FROM VISIT_DATE) = L


									              		AND EXTRACT(MONTH FROM VISIT_DATE) = #{month}
									                	AND EXTRACT(DAY FROM VISIT_DATE) = L


            								)) * 100, 'FM000.00')) "usage"
  FROM (
         SELECT LEVEL L
           FROM DUAL


	         	CONNECT BY LEVEL <= CASE WHEN #{year} = TO_CHAR(SYSDATE, 'YYYY') THEN EXTRACT(MONTH FROM SYSDATE)
	        												 			ELSE 12
	        												 END


	        	CONNECT BY LEVEL <= CASE WHEN #{year} || #{month} = TO_CHAR(SYSDATE, 'YYYYMM') THEN EXTRACT(DAY FROM SYSDATE)
	        												 			ELSE EXTRACT(DAY FROM LAST_DAY(TO_DATE(#{year} || #{month} || '01', 'YYYYMMDD')))
	        												 END


        )
