mapper.update:
update
	member
set
	user_name = #{ userName }
	, phone = #{ phone }
	, post_code = #{ postNumber }
	, address = #{ address }
	, detail_address = #{ detailAddress }
	, total_address = #{ totalAddress }
	, email = #{ email }
	, bank = #{ bank }
	, bank_account = #{ bankAccount }
	, modify_date = sysdate
	, modify_user_no = #{ userNo }
where
	user_no = #{ userNo }
