mapper.select:
SELECT COUNT(*) FROM MEMBER JOIN TB_GROUP D2 ON MEMBER.TEAM_CODE = D2.CODE LEFT JOIN TB_GROUP D1 ON D2.UPPER_CODE = D1.CODE AND D1.GROUP_CODE LIKE 'DEPT%' WHERE D1.STATUS = 'Y' AND D2.STATUS = 'Y'
AND D1.CODE_NAME = #{ department }
AND D2.CODE_NAME = #{ team }
AND PHONE LIKE '%' || #{ phone } || '%'
AND USER_NAME LIKE '%' || #{ name } || '%'

SELECT USER_NO , PROFILE_URL , USER_NAME , D1.CODE_NAME AS "DEPT" , D2.CODE_NAME AS "TEAM" , (SELECT CODE_NAME FROM TB_GROUP WHERE GROUP_CODE LIKE 'POSI%' AND CODE = MEMBER.POSITION_CODE) AS "POSI" , PHONE , EMAIL FROM MEMBER JOIN TB_GROUP D2 ON MEMBER.TEAM_CODE = D2.CODE LEFT JOIN TB_GROUP D1 ON D2.UPPER_CODE = D1.CODE AND D1.GROUP_CODE LIKE 'DEPT%' WHERE D1.STATUS = 'Y' AND D2.STATUS = 'Y'
AND D1.CODE_NAME = #{ department }
AND D2.CODE_NAME = #{ team }
AND PHONE LIKE '%' || #{ phone } || '%'
AND USER_NAME LIKE '%' || #{ name } || '%'
ORDER BY USER_NO ASC


        SELECT COUNT(*) 
		FROM TB_GROUP 
		WHERE CODE_NAME = #{teamName}
		  AND GROUP_CODE LIKE 'TEAM%';

mapper.update:
UPDATE TB_GROUP
   SET CODE_NAME = #{codeName}, MODIFY_DATE = SYSDATE, STATUS = 'N', MODIFY_EMP = #{modifyEmp}
 WHERE CODE = #{code} AND STATUS = 'N'
