mapper.select:
select USER_NAME
	from member
	where user_No = #{userNo}

select count(*) count from approval WHERE STATUS = 'Y' AND DOCUMENT_STATUS IN ('Y', 'N', 'I')
and DOCUMENT_TYPE IN ('T','C','B','M','J','G','H','V')
and DOCUMENT_TYPE = #{conditions}
and PAYMENT_STATUS IN ('보통','긴급')
and PAYMENT_STATUS = #{status}

select APPROVAL_NO , case when document_type = 'C' then '출장보고서' when document_type = 'H' then '휴직신청서' when document_type = 'T' then '퇴직신청서' when document_type = 'B' then '비품신청서' when document_type = 'J' then '지출결의서' when document_type = 'G' then '기안서' when document_type = 'M' then '매출보고서' else '휴가신청서' end document_type , DOCUMENT_NUMBER , DEPARTMENT , PAYMENT_WRITER , PAYMENT_WRITER_NO , (CASE WHEN DOCUMENT_STATUS = 'Y' THEN '승인완료' WHEN DOCUMENT_STATUS = 'I' THEN '진행중' WHEN DOCUMENT_STATUS = 'N' THEN '반려' END) DOCUMENT_STATUS ,(SELECT count(*) FROM SALES_REPORT S WHERE A.DOCUMENT_NUMBER = S.SALES_NO AND A.document_type = 'G' AND S.STATUS = 'Y' )SALES_STATUS , (SELECT count(*) FROM DRAFT_REPORT D WHERE A.DOCUMENT_NUMBER = D.DRAFT_NO AND A.document_type = 'J' AND D.STATUS = 'Y' ) DRAFT_STATUS , (SELECT count(*) FROM BUSINESSTRIP_REPORT B WHERE A.DOCUMENT_NUMBER = B.BUSINESS_NO AND A.document_type = 'C' AND B.STATUS = 'Y' ) BUSINESSTRIP_STATUS , to_char(regist_date, 'YYYY-MM-DD') regist_date , to_char(FINAL_APPROVAL_DATE, 'YYYY-MM-DD') FINAL_APPROVAL_DATE , PAYMENT_STATUS from approval A join member m on( USER_NO = PAYMENT_WRITER_NO) WHERE A.STATUS = 'Y' AND DOCUMENT_STATUS IN ('Y', 'N', 'I')
and DOCUMENT_TYPE IN ('T','C','B','M','J','G','H','V')
and DOCUMENT_TYPE = #{conditions}
and PAYMENT_STATUS IN ('보통','긴급')
and PAYMENT_STATUS = #{status}
order by APPROVAL_NO desc

   		select 
		     	count(*)
				from approval A
				WHERE STATUS = 'Y'
				AND DOCUMENT_STATUS IN ('Y', 'N', 'I')
		        and FIRST_APPROVAL = #{userName}
		        or MIDDLE_APPROVAL = #{userName}
		        or FINAL_APPROVAL = #{userName}

   		select 
		     		count(*)
				from approval A
				WHERE STATUS = 'Y'
				AND DOCUMENT_STATUS IN ('Y', 'N', 'I')
		        and FIRST_APPROVAL = #{userName}
