mapper.select:
SELECT (SELECT CODE_NAME FROM TB_GROUP WHERE GROUP_CODE LIKE 'TICK%' AND CODE = TICKET_TYPE ) "ticketType"
,TO_CHAR(PAYMENT_DATE, 'MM') "month"
,SUM(ORDER_PAYMENT) "sumSales" ,SUM(TICKET_CTN) "sumTicket" ,AVG(ORDER_PAYMENT) "avgSales" ,AVG(TICKET_CTN) "avgTicket" ,MAX(ORDER_PAYMENT) "maxSales" ,MAX(TICKET_CTN) "maxTicket" ,MIN(ORDER_PAYMENT) "minSales" ,MIN(TICKET_CTN) "minTicket" FROM TICKET_ORDER, TICKET_ORDER_DETAIL WHERE TICKET_ORDER.ORDER_NO = TICKET_ORDER_DETAIL.ORDER_NO AND ORDER_STATUS = 2 AND TO_CHAR(PAYMENT_DATE, 'YYYY') = #{year}
AND TO_CHAR(PAYMENT_DATE, 'MM') = #{month}
AND TO_CHAR(PAYMENT_DATE, 'DD') = #{date}
GROUP BY TICKET_ORDER_DETAIL.TICKET_TYPE
,TO_CHAR(PAYMENT_DATE, 'MM') ORDER BY "month"

  	SELECT 
          PAYMENT_METHOD
        ,(COUNT(ORDER_NO) / (SELECT COUNT(*) 
                               FROM TICKET_ORDER 
                              WHERE TO_CHAR(PAYMENT_DATE, 'YYYY') = 2024))  "percent"
	  FROM TICKET_ORDER
	 WHERE TO_CHAR(PAYMENT_DATE, 'YYYY') = #{year}

	   	AND TO_CHAR(PAYMENT_DATE, 'MM') = #{month}


	   	AND TO_CHAR(PAYMENT_DATE, 'DD') = #{date}

	 GROUP BY PAYMENT_METHOD
