mapper.select:
select VACATION_NO , VACATION_STRAT , VACATION_END , VACATION_COLOR , APPORVAL_STATUS , USER_NAME , CODE_NAME , UPPER_CODE from vacation join tb_group on (CODE = vacation.GROUP_CODE and tb_group.GROUP_CODE = 'CALD03') join Member on (USER_NO = vacation.MODIFY_EMP) where vacation.STATUS = 'Y' and APPORVAL_STATUS = 'Y' and member.team_code = #{teamCode}
and Months_between(VACATION_STRAT , sysdate) <= 6 and Months_between(VACATION_END , sysdate) >= -6
and vacation.MODIFY_EMP in (${userNO})

mapper.insert:
insert 
into vacation (
VACATION_NO
,VACATION_STRAT
,VACATION_END
,VACATION_COMMNET
,VACATION_COLOR
,REGIST_EMP
,MODIFY_EMP
,GROUP_CODE
)values (
	SEQ_VANO.nextval
	, to_date(#{vacaStart} , 'YYYY-MM-DD HH24:MI:SS')
	, to_date(#{vacaEnd}   , 'YYYY-MM-DD HH24:MI:SS')
	, #{vacaComment}
	, #{vacaColor}
	, #{member.userNo}
	, #{member.userNo}
	, #{group.code} 
)

mapper.update:
update 
	vacation
set 
	VACATION_STRAT = To_date(#{vacaStart}, 'YYYY-MM-DD HH24:MI:SS')
, VACATION_END = To_date(#{vacaEnd}, 'YYYY-MM-DD HH24:MI:SS')
, VACATION_COMMNET = #{vacaComment}
, MODIFY_EMP = #{member.userNo}
where 
    VACATION_NO = #{vacaNO}

mapper.select:
select VACATION_NO , VACATION_STRAT , VACATION_END , VACATION_COLOR , GROUP_CODE from vacation where STATUS = 'Y'
and GROUP_CODE = #{vacaGroupCode}
and to_char(VACATION_STRAT, 'YYYY-MM-DD') <= #{vacaStart} and to_char(VACATION_END, 'YYYY-MM-DD') >= #{vacaStart} and REGIST_EMP = #{member.userNo} and APPORVAL_STATUS = 'Y' order by VACATION_NO desc

mapper.update:
update
	vacation
set
	R_REQUEST_COMMENT = #{RRequestComent}
where
	VACATION_NO = #{vacaNO}
